(function() {
	window.Gallery = function(params) {
		var self = this;
		self.imgs = [];
		self.currentNum = -1;
		self.cover;
		self.carrier;
		
		self.settings = {
			view: {
				loop: false,
				cover: {
					css: {
						backgroundColor: "#333",
						opacity: .9,
						width: "100%",
						height: "100%",
						position: "fixed",
						top: "0px",
						left: "0px",
						zIndex: 90,
					},
				},
				carrier: {
					css: {
						width: "100%",
						height: "100%",
						position: "fixed",
						top: "0px",
						left: "0px",
						textAlign: "center",
						zIndex: 180
					}
				},
				close: {
					css: {
						position:"fixed",
						top:"20px",
						right:"20px",
						height: "20px",
						width: "20px",
						zIndex:200,
						cursor:"pointer",
						color: "#fff",
						backgroundRepeat:"no-repeat",
						backgroundPosition:"center top"
					},
					content: "&#10006;"
				},
				item: {
					css: {
						backgroundPosition: "center center",
						outline:"none",
						cursor:"pointer",
						backgroundRepeat: "no-repeat"
					}
				},
				slick: {
					dots: false,
					infinite: true,
					speed: 300,
					fade: true,
					slidesToShow: 1,
					arrows: true,
					afterChange: function() {
						self.currentNum++;
					/*	if (self.currentNum >= self.imgs.length) {
							$('.slick-next').css('pointer-events', 'none');
						}*/
					},
					nextArrow:"<div class = 'slick-next' style = 'width:11px;height:19px;background:url(/_components/folderGallery/arrows.png);background-position:right top;right:20px;'> </div>",
					prevArrow:"<div class = 'slick-prev' style = 'width:11px;height:19px;background:url(/_components/folderGallery/arrows.png);background-position:left top;left:20px;'> </div>"
				}
			},
			main: {
				slick: {
					dots: false,
					infinite: true,
					speed: 300,
					slidesToShow: 3,
					arrows: true,
					nextArrow:"<div class = 'slick-next' style = 'width:11px;height:19px;background:url(/_components/folderGallery/arrows.png);background-position:right top;right:20px;'> </div>",
					prevArrow:"<div class = 'slick-prev' style = 'width:11px;height:19px;background:url/_components/folderGallery/arrows.png);background-position:left top;left:20px;'> </div>",
					adaptiveHeight: true,
					autoplay: false,
					autoplaySpeed: 5000,
					cssEase: 'linear',
					responsive: [
					{
					  breakpoint: 768,
					  settings: {
						arrows: false,
						slidesToShow: 1
					  }
					},
					{
					  breakpoint: 450,
					  settings: {
						arrows: false,
						centerMode: false,
						slidesToShow: 1
					  }
					}
				  ]
				}
			}
		};

		self.settings = $.extend(true, self.settings, params); //объединение параметров

		$(".galleryPreview").on("click", function(event) {
			var target = event.target;
			
			if (!$(target).hasClass("galleryPreview")) return;
			
			var parent = $(target).parent(".gallery");

			var galleryStr = $(target).closest(".gallery").attr('data-gallery');

			var imgs = JSON.parse(galleryStr);

			self.start(target.getAttribute("data-img"), imgs);	
		});
		
		self.start = function(current, imgs) {
			for (var i = 0; i < imgs.length; i++) {
				if (imgs[i].src === current) self.currentNum = i;
			}
			
			if (self.currentNum < 0) return;
			
			self.imgs = imgs;
			
			/*фон*/ 
			self.cover = document.createElement("div");
			self.cover.id = "galleryViewCover";
			document.body.appendChild(self.cover);
			var coverOpacity = self.settings.view.cover.css.opacity;
			self.settings.view.cover.css.opacity = 0;
			$("#galleryViewCover").css(self.settings.view.cover.css);
			$("#galleryViewCover").animate({
				opacity: coverOpacity
			}, 500, function() {
				self.settings.view.cover.css.opacity = coverOpacity;
			});
			/*/фон*/ 
			
			/*контейнер*/
			self.carrier = document.createElement("div");
			self.carrier.id = "galleryViewCarrier";
			document.body.appendChild(self.carrier);
			$('#galleryViewCarrier').css(self.settings.view.carrier.css);
			self.carrier.innerHTML = "<div id = 'closeGallery'>" + self.settings.view.close.content + "</div><div id = 'viewCarrier'></div>";
			/*/контейнер*/

			/*Кнопка закрыть*/
			$('#closeGallery').css(self.settings.view.close.css);
			if (self.settings.view.close.hover) {
				$('#closeGallery').hover(function() {
					$('#closeGallery').css(self.settings.view.close.hover.css);
				});
				$('#closeGallery').mouseout(function() {
					$('#closeGallery').css(self.settings.view.close.css);
				});
			}
			$('#closeGallery').on("click", function() {
				self.hide();
			});
			/*/Кнопка закрыть*/
			
			self.scrolled = $(window).scrollTop();

			$("body").css({	
				overflow: "hidden",
				position: "fixed",
				left: 0,
				right:0,
				top: -1 * $(window).scrollTop() + "px"
			});
			$("body").on("scroll", function() {
				return false;
			});

			window.onresize = function() {
				self.resizeBlock();
			}
			/*
			window.onpopstate = function(event) {
				event.preventDefault();
				self.hide();
			});
			*/
			var html = "";
			for (var key in self.imgs) {
				html += "<div class = 'item' style = 'background-image:url(" + self.imgs[key]['src'] + ");'></div>";
			}
			$("#viewCarrier").html(html);
			
			
			$("#viewCarrier .item").css($.extend({height: self.getClientHeight() + "px"}, self.settings.view.item.css));

			$('#viewCarrier').on('init', function(event, slick) {
				$(".slick-next").hover(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "right bottom"
					});
				});
				$(".slick-prev").hover(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "left bottom"
					});
				});
				$(".slick-next").mouseout(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "right top"
					});
				});
				$(".slick-prev").mouseout(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "left top"
					});
				});
			});
			
			$("#viewCarrier .item").on("click", function() {
				$('#viewCarrier').slick("slickNext");
			});

			$('#viewCarrier').slick(self.settings.view.slick);
			
			$('#viewCarrier').on("mousewheel", function(event) {
				event.preventDefault();
				
				if (event.deltaX > 0 || event.deltaY < 0) {
					console.log("scrollNext");
					$('#viewCarrier').slick("slickNext");
				} else if (event.deltaX < 0 || event.deltaY > 0) {
					console.log("scrollPrev");
					$('#viewCarrier').slick("slickPrev");
				}
			});

			$('#viewCarrier').slick("goTo", self.currentNum, true);
			
			$(document).keyup(function(e) {
				 if (e.keyCode == 27) { 
					self.hide();
				}
			});
			
		//	self.show();
		}

		self.hide = function() {
			$('#viewViewCarrier').slick("unslick");
			
			$('#galleryViewCover').empty();
			$('#galleryViewCover').remove();
			$('#galleryViewCarrier').empty();
			$('#galleryViewCarrier').remove();

			$("body").css({	
				overflow: "auto",
				position: "inherit",
				left: "auto",
				right:"auto",
				top: "auto"
			});
			$(window).scrollTop(self.scrolled);
			$("body").off("scroll", function() {
				return false;
			});
			$(window).off("popstate", function(e) {
				self.hide();
			});
		}

		self.getClientHeight = function() {
			return window.innerHeight;
		}

		self.getClientWidth = function() {
			return document.documentElement.clientWidth;
		}

		self.resizeBlock = function() {
			if (self.getClientWidth() >= self.imgs[self.currentNum].width && self.getClientHeight() >= self.imgs[self.currentNum].height) {
				$("#viewCarrier .item").css({
					height: self.getClientHeight() + "px",
					backgroundSize:"auto"
				});
			} else {
				$("#viewCarrier .item").css({
					height: self.getClientHeight() + "px",
					backgroundSize:"contain"
				});
			}
		}
		
		self.setSlick = function() {
			$(document).ready( function() {
				if (!$('.gallery').attr("data-slick")) return;
				$('.gallery').slick(self.settings.main.slick);
				$('.gallery .slick-dots ').css({"bottom": "inherit"})
				
				$(".slick-next").hover(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "right bottom"
					});
				});
				$(".slick-prev").hover(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "left bottom"
					});
				});
				$(".slick-next").mouseout(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "right top"
					});
				});
				$(".slick-prev").mouseout(function(event) {
					var target = event.target;
					$(target).css({
						backgroundPosition: "left top"
					});
				});
			});
		}
		
		self.setSlick();
	}
})();

